INSERT INTO `send_remind_mail_pattern_info` VALUES (299, 581, '【WATASHIBA】Lesson Reservation Completed', 'Dear #STUDENT_NAME#.\n\nThank you for taking the WATASHIBA.\nWe have received your reservation for the following lesson.\nThe details of your reservation are as follows. Please check it once again.\n\n------------------------------------------------------------------------\nLesson date: #LESSON_DATE#\nLesson start time: #LESSON_TIME#\nLesson name: #LESSON_NAME#\nCourse name: #COURSE_NAME#\nLesson text name: #LESSON_TEXT_NAME#\nLecturer nickname: #TEACHER_NICKNAME#\n-------------------------------------------------------------------------\n\nWhen it is 5 minutes before the lesson starts, please login to the My Page and click the Start Lesson button to start the lesson.\n My page login link: \n#STUDENT_MY_PAGE_URL#\n\nThe lesson will be conducted via zoom, so if you do not have the zoom app installed, please install it on your device in advance.\n#ZOOM_MANUAL_URL#\n\nYou can check and change your registration information from \"\"Basic Information\"\" in \"\"Change Registration Information\"\" on My Page.\n#STUDENT_SETTING_URL#\n\nIf you do not hear from your tutor when the lesson time comes, please contact the Customer Service Center.\n\nWe look forward to seeing #STUDENT_NAME# at the lesson.\n\nPlease do not reply to this email as this is a send-only address. \nWe are unable to respond to your inquiry even if you reply to this email.\n\n.....................................................................................................................\nContact information\n\n.....................................................................................................................', 'en');
INSERT INTO `send_remind_mail_pattern_info` VALUES (301, 591, '【WATASHIBA】Withdrawal Completed.', 'Dear #STUDENT_NAME#.\n\nThank you for using our WATASHIBA.\nThe withdrawal process has been completed.\nPlease note that the remaining number of lessons will no longer be valid.\n\nIf you have any questions about the withdrawal process, please contact us from the following link.\n\nContact page: https://watashiba.jp/contact/\n\nWe are looking forward to seeing you again.\n\nPlease do not reply to this email as this is a send-only address. \nWe are unable to respond to your inquiry even if you reply to this email.\n\n.....................................................................................................................\nContact information\n\n.....................................................................................................................', 'en');
INSERT INTO `send_remind_mail_pattern_info` VALUES (303, 601, '【WATASHIBA】30 Minutes Before The Lesson Starts', 'Dear #STUDENT_NAME#.\n\nThank you for taking the \"WATASHIBA\".\nThe lesson reserved by #STUDENT_NAME# is now only 30 minutes away.\nThe details of your reservation are as follows. Please check it once again.\n\n------------------------------------------------------------------------\nLesson date: #LESSON_DATE#\nLesson start time: #LESSON_TIME#\nLesson name: #LESSON_NAME#\nLesson text name: #LESSON_TEXT_NAME#\nLecturer nickname: #TEACHER_NICKNAME#\n-------------------------------------------------------------------------\n\nWhen it is 5 minutes before the lesson starts, please log in to My Page and click the Start Lesson button to start the lesson.\n#STUDENT_MY_PAGE_URL#\n\nThe lesson will be conducted via zoom, so if you do not have the zoom app installed, please install it on your device in advance.\n#ZOOM_MANUAL_URL#\n\nYou can check and change your registration information from \"\"Basic Information\"\" in \"\"Change Registration Information\"\" on My Page.\n\nIf you do not receive a call from your tutor at the time of your lesson, please contact the Customer Service Center.\n\nWe look forward to seeing #STUDENT_NAME# at your lesson.\n\nPlease do not reply to this email as this is a send-only address.\nWe are unable to respond to your inquiry even if you reply to this email. \n .....................................................................................................................\nContact Information\n\n .....................................................................................................................', 'en');
INSERT INTO `send_remind_mail_pattern_info` VALUES (305, 611, '【WATASHIBA】Course Purchase Completed', 'Dear #STUDENT_NAME#.\n\nThank you for taking the \"WATASHIBA\" course.\n\nYour course purchase has been completed.\nPlease find the purchase details below. Please check it once again.\n\n------------------------------------------------------------------------\nCourse Name: #COURSE_NAME#\nNumber of lessons: #COURSE_COUNT#\nPrice: #COURSE_PRICE#\nPayment method: #COURSE_PAYMENT#\n-------------------------------------------------------------------------\n\nPlease login to My Page to reserve a lesson.\n#STUDENT_MY_PAGE_URL#\n\nThe lesson will be conducted via zoom, so if you do not have the zoom app installed, please install it on your device in advance.\n#ZOOM_MANUAL_URL#\n\nPlease do not reply to this email as this is a send-only address. \nWe are unable to respond to your inquiry even if you reply to this email.\n\n.....................................................................................................................\nContact information\n\n.....................................................................................................................', 'en');
