INSERT INTO `send_remind_mail_pattern_info` VALUES (315, 661, '【WATASHIBA】Thank You for Your Inquiry.', 'Dear #USER_TITLE#.\nThank you for your inquiry about our WATASHIBA.\nWe have received your inquiry, and the details are as below.\n------------------------------------------------------------------------\n\nInquiry ID: #INQUIRY_ID#\n\nDate and time: #INQUIRY_DATE#\n\nSubject of inquiry: #INQUIRY_TITLE#\n\nYour name: #USER_TITLE#\n\nE-mail address：#USER_EMAIL#\n\nInquiry contents: #INQUIRY_BODY#\n\n-------------------------------------------------------------------------\n\nA reply to your inquiry will be sent to the email address above.\n\nPlease do not reply to this email as this is a send-only address. \nWe are unable to respond to your inquiry even if you reply to this email. \n\n.....................................................................................................................\nContact us\n\n .....................................................................................................................', 'en');
INSERT INTO `send_remind_mail_pattern_info` VALUES (317, 501, '【Học tiếng nhật trực tuyến ALC】　　Đăng ký tạm đã hoàn thành', '#STUDENT_NAME# thân mến\r\rChúng tôi xin chân thành cảm ơn bạn đã đăng ký tài khoản sử dụng hệ thống học tiếng nhật trực tuyến của chúng tôi.\r\rViệc đăng ký tài khoản tạm đã hoàn thành.\r\rXin vui lòng kích hoạt tài khoản của bạn thông qua đường dẫn dưới đây.\r#CONFIRM_URL#\r\rTrường hợp bạn không truy cập vào được đường dẫn trên, xin vui lòng copy và dán đường dẫn dưới đây lên trình duyệt và nhấn phím enter.\r<#CONFIRM_URL#>\r\rSau khi đã kích hoạt tài khoản xong, xin hãy truy cập vào đường dẫn dưới đây để xem cách sử dụng hệ thống.\rhttp://goo.gl/G0qgvV\r\rNếu có bất kỳ sự khó hiểu hoặc câu hỏi gì xin hãy vui lòng liên hệ với chúng tôi thông qua mẫu liên hệ.\r\rChúng tôi rất mong có được những bài học giữa #STUDENT_NAME# và giáo viên của chúng tôi.\r\r………………………………………………………………………………………………………\rCông ty cổ phần ALC\rBan thư ký trang học tiếng nhật trực tuyến ALC\rMẫu liên lạc ：https://nihongo-vn.alc.co.jp/user/contact\r………………………………………………………………………………………………………\r\r\r\r\r', 'vn');
INSERT INTO `send_remind_mail_pattern_info` VALUES (319, 511, '【Học tiếng nhật trực tuyến ALC】 Huỷ bỏ đăng ký bài học đã hoàn thành', '#STUDENT_NAME# thân mến\r\rĐầu tiên chúng tôi xin chân thành cảm ơn bạn đã sử dụng hệ thống học tiếng nhật trực tuyến của chúng tôi.\r\r\rThông tin về bài học huỷ bỏ như sau:\r----------------------------------------------------------------------\rNgày học： #LESSON_DATE#\rGiờ học： #LESSON_TIME#\rTên bài học： #LESSON_NAME#\rTên tài liệu：#LESSON_TEXT_NAME#\rGiáo viên:　#TEACHER_NICKNAME#\r----------------------------------------------------------------------\r\rMong hẹn gặp lại #STUDENT_NAME# trong những bài học tới cùng với hệ thống giáo viên của chúng tôi.\r\rXin chân thành cảm ơn.\r………………………………………………………………………………………………………\rCông ty cổ phần ALC\rBan thư ký trang học tiếng nhật trực tuyến ALC\rMẫu liên lạc ：https://nihongo-vn.alc.co.jp/user/contact\r………………………………………………………………………………………………………\r', 'vn');
INSERT INTO `send_remind_mail_pattern_info` VALUES (321, 521, '【Học tiếng nhật trực tuyến ALC】　Thông báo đã thay đổi mật khẩu', '#STUDENT_NAME# thân mến\r\rĐầu tiên chúng tôi xin chân thành cảm ơn bạn đã sử dụng hệ thống học tiếng nhật trực tuyến của chúng tôi.\r\rViệc thay đổi mật khẩu của bạn đã được hoàn thành.\r\r\rXin cảm ơn.\r………………………………………………………………………………………………………\rCông ty cổ phần ALC\rBan thư ký trang học tiếng nhật trực tuyến ALC\rMẫu liên lạc ：https://nihongo-vn.alc.co.jp/user/contact\r………………………………………………………………………………………………………\r', 'vn');
