INSERT INTO `send_remind_mail_pattern_info` VALUES (325, 541, '【Học tiếng nhật trực tuyến ALC】 Thay đổi mật khẩu', 'Xin chào #TEACHER_NAME#\r\rĐể thay đổi mật khẩu, xin hãy truy cập vào đường dẫn dưới đây:\r\r#CHANGEPASS_URL#\r\rXin chân thành cảm ơn.\r\r', 'vn');
INSERT INTO `send_remind_mail_pattern_info` VALUES (327, 551, '【アルクオンライン日本語スクール】　パスワードを変更します。', '##ADMIN_NAME# 様。\r\rこの度は、「アルクオンライン日本語スクール」のご受講ありがとうございます。\rアルクオンライン日本語スクール事務局です。\r\rパスワードの変更を下記のURLより行ってください。\r\r#CHANGEPASS_URL#\r\r変更されましたパスワードは、お忘れないようお控ください。\r\rよろしくお願いいたします。\r\r………………………………………………………………………………………………………\r株式会社アルク\rアルク オンライン日本語スクール事務局\rお問い合わせフォーム：https://nihongo-vn.alc.co.jp/user/contact\r………………………………………………………………………………………………………', 'vn');
INSERT INTO `send_remind_mail_pattern_info` VALUES (333, 581, '【Học tiếng nhật trực tuyến ALC】 Đăng ký bài học đã hoàn thành', '#STUDENT_NAME# thân mến\r\rĐầu tiên chúng tôi xin chân thành cảm ơn bạn đã sử dụng hệ thống học tiếng nhật trực tuyến của chúng tôi.\r\r\rNội dung thông tin của bài học mà #STUDENT_NAME# đã đăng ký như sau:\r--------------------------------------------------------------\rNgày học ：#LESSON_DATE#\rGiờ học ：#LESSON_TIME#\rTên bài học：#LESSON_NAME#\rTên tài liệu：#LESSON_TEXT_NAME#\rGiáo viên:　#TEACHER_NICKNAME#\r--------------------------------------------------------------\rChúng tôi xin lưu ý rằng việc huỷ bỏ đăng ký bài phải thực hiện trước 24 tiếng đồng hồ khi bài học bắt đầu. \r\rXin chúc cho #STUDENT_NAME#  sẽ có 1 bài học thật vui vẻ và hiệu quả với hệ thống giáo viên của chúng tôi.\r\rXin chân thành cảm ơn.\r………………………………………………………………………………………………………\rCông ty cổ phần ALC\rBan thư ký trang học tiếng nhật trực tuyến ALC\rMẫu liên lạc ：https://nihongo-vn.alc.co.jp/user/contact\r………………………………………………………………………………………………………\r\r\r', 'vn');
INSERT INTO `send_remind_mail_pattern_info` VALUES (335, 591, 'Hủy tài khoản sử dụng hệ thống Học tiếng anh trực tuyến ALC', '#STUDENT_NAME# Thân mến\r\rXin chân thành cảm ơn đã sử dụng hệ thống của chúng tôi.\r\rĐể hủy tài khoản sử dụng hệ thống, hãy tiến hành theo đường dẫn dưới đây: \r#CONFIRM_URL#\r\rXin chân thành cảm ơn.', 'vn');
INSERT INTO `send_remind_mail_pattern_info` VALUES (337, 601, '【Học tiếng nhật trực tuyến ALC】 Thông báo còn 30 phút nữa giờ học bắt đầu', '#STUDENT_NAME# thân mến\r \rĐầu tiên chúng tôi xin chân thành cảm ơn bạn đã sử dụng hệ thống học tiếng nhật trực tuyến của chúng tôi.\r\rBài học mà #STUDENT_NAME# đã đang ký chì còn 30 phút nữa là sẽ được bắt đầu, dưới đây là thông tin về bài học săp bắt đầu, xin bạn hãy kiểm tra lại 1 lần nữa:\r\r------------------------------------------------------------------------\rNgày học：#LESSON_DATE#\rThời gian bắt đầu ：#LESSON_TIME#\rTên bài học：#LESSON_NAME#\rTên tài liệu：#LESSON_TEXT_NAME#\rGiáo viên:　#TEACHER_NICKNAME#\r-------------------------------------------------------------------------\r\rTrước khi bắt đầu học 5 phút, xin bạn hãy vui lòng đăng nhập sẵn vào skype và đợi cuộc gọi từ giáo viên của chúng tôi.\rNào và bay giờ xin chúc #STUDENT_NAME# sẽ có 1 bài học thật vui vẻ và hiệu quả với giáo viên của chúng tôi.\r\rXin chân thành cảm ơn.\r………………………………………………………………………………………………………\rCông ty cổ phần ALC\rBan thư ký trang học tiếng nhật trực tuyến ALC\rMẫu liên lạc ：https://nihongo-vn.alc.co.jp/user/contact\r………………………………………………………………………………………………………', 'vn');
INSERT INTO `send_remind_mail_pattern_info` VALUES (339, 611, '【Học tiếng nhật trực tuyến ALC】　Mua khóa học đã hoàn thành', '#STUDENT_NAME# thân mến\r\rĐầu tiên chúng tôi xin chân thành cảm ơn bạn đã sử dụng hệ thống học tiếng nhật trực tuyến và mua khóa học của chúng tôi.\r\rThông tin về khóa học mà #STUDENT_NAME#  đã mua như sau:\r\r--------------------------------------------------------------\r\rMã số đặt hàng：#ORDER_ID#\r\rMã số khoá học ：#COURSE_ID#\r\rTên khoá học ：#COURSE_NAME#\r\rLệ phí khóa học ：#COURSE_PRICE#　円\r\r(Thuế)：#COURSE_TAX#　円\r\rTổng tiền thanh toán：#COURSE_TOTAL#　円\r\rNgày giờ thanh toán：#ORDER_DATE#\r\rThời hạn sử dụng khoá học：#EXPIRE_DATE#\r\r\rXin chân thành cảm ơn.\r………………………………………………………………………………………………………\rCông ty cổ phần ALC\rBan thư ký trang học tiếng nhật trực tuyến ALC\rMẫu liên lạc ：https://nihongo-vn.alc.co.jp/user/contact\r………………………………………………………………………………………………………', 'vn');
INSERT INTO `send_remind_mail_pattern_info` VALUES (341, 621, 'レッスンキャンセル通知', ' #TEACHER_NAME# 先生\r\r以下のレッスンがキャンセルされました。\r\r----------------------------------------------------------------------\r\rレッスン日：#LESSON_DATE#\r\rレッスン時間：#LESSON_TIME#\r\rレッスン名：#LESSON_NAME#\r\rテキスト名 ：#LESSON_TEXT_NAME#\r\r----------------------------------------------------------------------\r\rこのメールは自動送信メールです。\r（このメールに返信しないでください）\r\r\rTeacher\'s Page URL\rhttps://nihongo-system.alc.co.jp/tutor.swf\r', 'vn');
INSERT INTO `send_remind_mail_pattern_info` VALUES (345, 641, 'レッスン30分前通知', ' #TEACHER_NAME# 先生\r\r下記レッスンまで30分となりました。\r生徒名:　#STUDENT_NICKNAME#\rレッスン日:　#LESSON_DATE#\rレッスン時間:　#LESSON_TIME#\rレッスン名:　#LESSON_NAME#\rテキスト名:　#LESSON_TEXT_NAME#\r\rこのメールは自動送信メールです。\r（このメールに返信しないでください）\r\r\rTeacher\'s Page URL\rhttps://nihongo-system.alc.co.jp/tutor.swf\r', 'vn');
INSERT INTO `send_remind_mail_pattern_info` VALUES (347, 651, 'レッスン予約通知', ' #TEACHER_NAME# 先生\r\rあなたのレッスンが予約されました。\r\r生徒名:　#STUDENT_NICKNAME#\r\rレッスン日:　#LESSON_DATE#\rレッスン時間:　#LESSON_TIME#\rレッスン名:　#LESSON_NAME#\rテキスト名:　#LESSON_TEXT_NAME#\r\rこのメールは自動送信メール。\r（このメールに返信しないでください）\r\r\rTeacher\'s Page URL\rhttps://nihongo-system.alc.co.jp/tutor.swf\r\r\r', 'vn');
INSERT INTO `send_remind_mail_pattern_info` VALUES (349, 661, '【アルクオンライン日本語スクール】　新しいお問い合わせが来ました', '#ADMIN_NAME#様\r\r\r\r新しいお問い合わせがありました。\r\rお問い合わせの内容は、以下となります。\r\r\r\r------------------------------------------------------------------------\r\rお問い合わせID：#INQUIRY_ID#\r\r日時：#INQUIRY_DATE#\r\rお問い合わせ件名：#INQUIRY_TITLE#\r\rお名前：#USER_TITLE#\r\rメールアドレス：#USER_EMAIL#\r\r問い合わせ内容：#INQUIRY_BODY#\r\r-------------------------------------------------------------------------\r\r\r\rアルクオンライン日本語スクール事務局\r\r……………………………………………………………………\r\r株式会社アルク　\r\rアルクオンライン日本語スクール事務局\r\rjpn_info@alc.co.jp\r\r……………………………………………………………………', 'vn');
INSERT INTO `send_remind_mail_pattern_info` VALUES (350, 315, '【WATASHIBA】Comments from the lecturers has been received', 'Dear #STUDENT_NAME#.\n\nThank you for taking the lesson, WATASHIBA.\nWe have received comments from the lecturer for the lesson taken by #STUDENT_NAME#.\nThe lesson details are as follows.\n\n------------------------------------------------------------------------\nLesson date: #LESSON_DATE#\nLesson start time: #LESSON_TIME#\nLesson name: #LESSON_NAME#\nLesson text name: #LESSON_TEXT_NAME#\nLecturer nickname: #TEACHER_NICKNAME#\n-------------------------------------------------------------------------\n\nPlease login to My Page to see the comments.\n#STUDENT_MY_PAGE_URL#\n\nPlease do not reply to this email as this is a send-only address. \nWe are unable to respond to your inquiry even if you reply to this email.\n\n.....................................................................................................................\nContact information\n\n.....................................................................................................................', 'en');
INSERT INTO `send_remind_mail_pattern_info` VALUES (353, 100, '【WATASHIBA】Course Purchase Completed', 'Dear #STUDENT_NAME#.\n\nThank you for taking the \"WATASHIBA\" course.\n\nYour course application has been completed.\nPlease find below the details of your application.\n\n------------------------------------------------------------------------\nCourse name: #COURSE_NAME#\nNumber of lessons: #COURSE_COUNT#\n-------------------------------------------------------------------------\n\nPlease login to My Page to check your course details.\n My Page Login Link: \n\nIf you do not have the zoom app installed, please install it on your device in advance. \n Link for zoom usage instructions, etc. \n\nPlease do not reply to this email as this is a send-only address. \nWe are unable to respond to your inquiry even if you reply to this email.\n.....................................................................................................................\nContact information\n\n.....................................................................................................................', 'en');
INSERT INTO `send_remind_mail_pattern_info` VALUES (356, 171, '【WATASHIBA】Free Registration Completed', 'Dear #STUDENT_NAME#.\n\nThank you for registering your \"WATASHIBA\".\nWe will send you a Password.\n\n------------------------------------------------------------------------\nPassword: #STUDENT_PASSWORD#\n-------------------------------------------------------------------------\n\nPlease login to My Page to check your registration details.\n My Page Login Link: \n\nThe lesson will be conducted via zoom, so if you do not have the zoom app installed, please install it on your device in advance.\n Link for zoom usage instructions, etc. \n\nPlease do not reply to this email as this is a send-only address. \nWe are unable to respond to your inquiry even if you reply to this email.\n\n.....................................................................................................................\nContact information\n.....................................................................................................................', 'en');
INSERT INTO `send_remind_mail_pattern_info` VALUES (363, 664, '【服務名稱】實力測試評估請求', '提交了下述能力測試。\n\n請登錄講師我的頁麵要求進行評估。\n\n能力測試課程：#COURSE_NAME#\n評估期限（JST）：#TEST_LIMIT_DATE##TEST_LIMIT_TIME#\n\n講師主頁\n#MY_PAGE_URL#\n\n※此郵件是從一個隻發送的電子郵件地址發出的。\n   所以，無法回複您的電子郵件。\n……………………………………………………………………………………………\n連絡先\n\n……………………………………………………………………………………………', 'zh');
INSERT INTO `send_remind_mail_pattern_info` VALUES (364, 665, '【服務名稱】實力測試評估請求', '現在還剩下24小時來評估以下能力測試。\r\n請登錄講師我的頁麵要求進行評估。\r\n\r\n能力測試課程名稱：#COURSE_NAME#\r\n評價期限（JST）：#TEST_LIMIT_DATE##TEST_LIMIT_TIME#\r\n\r\n講師主頁\r\nマイページ URL\r\n※此郵件是從一個隻發送的電子郵件地址發出的。\r\n   所以，無法回複您的電子郵件。\r\n…………………………………………………………………………………\r\n連絡先\r\n\r\n…………………………………………………………………………………', 'zh');
INSERT INTO `send_remind_mail_pattern_info` VALUES (365, 666, '【WATASHIBA】Notification of Voided Group Lesson', 'Dear #STUDENT_NAME#.\nThank you for using \"WATASHIBA\".\nWe are very sorry to inform you that the course you signed up for will not be held as it did not reach the minimum number of participants.\n\n------------------------------------------------------------------------\nCourse Name: #COURSE_NAME#\n-------------------------------------------------------------------------\n\nNo lesson fee will be charged for the aforementioned course.\n\nWe look forward to seeing #STUDENT_NAME# in near future.\n\nPlease do not reply to this email as this is a send-only address. \nWe are unable to respond to your inquiry even if you reply to this email.\n\n.....................................................................................................................\nContact information\n\n.....................................................................................................................', 'en');
INSERT INTO `send_remind_mail_pattern_info` VALUES (366, 666, '【わたしば】小组課程取消通知', '#STUDENT_NAME#學員\n\n感謝您使用“わたしば”。\n您申請的課程因未達到開課標準的人數而中止舉辦。 \n------------------------------------------------------------------------\n學科課程名稱：#COURSE_NAME#\n-------------------------------------------------------------------------\n\n取消課程的費用將不予支付。\n\n#STUDENT_NAME# 歡迎您再次參加。\n\n※此郵件是從一個隻發送的電子郵件地址發出的。\n   所以，無法回複您的電子郵件。\n………………………………………………………………………………………………………\n連絡先', 'zh');
INSERT INTO `send_remind_mail_pattern_info` VALUES (367, 667, '【WATASHIBA】Notice of Group Course Approval', 'Dear #STUDENT_NAME#.\n\nThank you for your interest in WATASHIBA.\nWe are pleased to inform you that the course you have applied for has been approved.\n\n------------------------------------------------------------------------\nCourse name: #COURSE_NAME#\n------------------------------------------------------------------------\n\nPlease log in to My Page to confirm the detailed schedule of the course.\n#STUDENT_MY_PAGE_URL#\n\nPlease do not reply to this email as this is a send-only address. \nWe are unable to respond to your inquiry even if you reply to this email.\n\n .....................................................................................................................\nContact\n .....................................................................................................................', 'en');
INSERT INTO `send_remind_mail_pattern_info` VALUES (368, 667, '【WATASHIBA】團體開課通知', '#STUDENT_NAME #\n\n感謝您使用“WATASHIBA”。\n申请的课程已经决定開課。\n\n------------------------------------------------------------------------\n課程名稱：#COURSE_NAME #\n------------------------------------------------------------------------\n\n請登錄我的頁面查看課程的詳細時間表。\n#STUDENT_MY_PAGE_URL#\n\n* 此電子郵件是從僅發送電子郵件地址發送的。\n請注意，即使您回复我們也無法回复，敬请谅解。\n\n………………………………………………………………………………………………………\n聯繫地址\n\n………………………………………………………………………………………………………', 'zh');
INSERT INTO `send_remind_mail_pattern_info` VALUES (369, 591, '【わたしば】提現已完成', '#STUDENT_NAME#學員\n\n感謝您聽講“わたしば”。\n“わたしば”已完整退出程序。\n \n退出時剩餘的聽課次數全部無效，敬請諒解。\n\n關於退會手續如果有不明白的地方的話，麻煩您從下麵的聯係方式聯係。\n\n聯係人頁麵: https://watashiba.jp/contact/\n\n衷心期待您的再次光臨。\n\n※此郵件是從一個隻發送的電子郵件地址發出的。\n   所以，無法回複您的電子郵件。\n………………………………………………………………………………………………………\n連絡先\n\n………………………………………………………………………………………………', 'zh');
