INSERT INTO `send_remind_mail_pattern_info` VALUES (283, 501, '【WATASHIBA】Free Member Registration Completed', 'Dear #STUDENT_NAME#.\n\nThank you for registering your \"WATASHIBA\".\n\nOnce you register, you will be able to take a free trial lesson.\nPlease login to My Page to reserve your free trial lesson.\n#STUDENT_MY_PAGE_URL#\n\nThe lesson will be conducted via zoom, so if you do not have the zoom app installed, please install it on your device in advance.\n#ZOOM_MANUAL_URL#\n\nThis email has been sent from a send-only address. \nPlease note that we are unable to check or reply to you even if you reply to this e-mail.\nーーーーーーーーーーーーーーーーーーーーーー\n【Contact】\nTIJ Tokyo Institute of Japanese\nWatashiba Management Office（https://watashiba.jp）\nーーーーーーーーーーーーーーーーーーーーーー', 'en');
INSERT INTO `send_remind_mail_pattern_info` VALUES (285, 511, '【WATASHIBA】Lesson Cancellation Completed', 'Dear #STUDENT_NAME#.\n\nThank you for using \"WATASHIBA\".\nWe have received your cancellation request for the following lesson.\n----------------------------------------------------------------------\nLesson date: #LESSON_DATE#\nLesson time: #LESSON_TIME#\nLesson name: #LESSON_NAME#\nLesson text name: #LESSON_TEXT_NAME#\nLecturer nickname: #TEACHER_NICKNAME#\n----------------------------------------------------------------------\n\nWe look forward to hearing from #STUDENT_NAME# again soon.\n\nThis email has been sent from a send-only address. \nPlease note that we are unable to check or reply to you even if you reply to this e-mail.\nーーーーーーーーーーーーーーーーーーーーーー\n【Contact】\nTIJ Tokyo Institute of Japanese\nWatashiba Management Office（https://watashiba.jp）\nーーーーーーーーーーーーーーーーーーーーーー', 'en');
INSERT INTO `send_remind_mail_pattern_info` VALUES (287, 521, '【WATASHIBA】Password Change', 'Dear #STUDENT_NAME#.\n\nThank you for using \"WATASHIBA\".\nYour login password has been changed.\nFor the security, the password will not be included in this e-mail.\n\nLogin ID (e-mail address): #STUDENT_MAIL#\n\nMy Page Login Link: #STUDENT_MY_PAGE_URL#\nIf you have received this e-mail in error, please delete it.\n\nThis email has been sent from a send-only address. \nPlease note that we are unable to check or reply to you even if you reply to this e-mail.\nーーーーーーーーーーーーーーーーーーーーーー\n【Contact】\nTIJ Tokyo Institute of Japanese\nWatashiba Management Office（https://watashiba.jp）\nーーーーーーーーーーーーーーーーーーーーーー', 'en');
INSERT INTO `send_remind_mail_pattern_info` VALUES (289, 531, '【WATASHIBA】Password Reissue', 'Password reissue has been accepted.\nPlease access the following URL and set a new password.\n\n#CHANGEPASS_URL#\n\nIf you have received this e-mail in error, please delete it.\n\nThis email has been sent from a send-only address. \nPlease note that we are unable to check or reply to you even if you reply to this e-mail.\nーーーーーーーーーーーーーーーーーーーーーー\n【Contact】\nTIJ Tokyo Institute of Japanese\nWatashiba Management Office（https://watashiba.jp）\nーーーーーーーーーーーーーーーーーーーーーー', 'en');
